import React from 'react';
import axios from 'axios';




class FindAlbum extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      albumList:[]
    }
  }

search(e){
  const value = e.currentTarget.value
  this.setState({
    searchValue: value
  })
  if(value === ''){
  this.setState({
  albumList: []
})
}else{
  axios({
    url: `https://api.spotify.com/v1/search?q=${value}&type=track&limit=10`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>{ 
    console.log(resp)
    this.setState({
      albumList: resp.data.tracks.items
     })
  }).catch(error => (new Error(console.log(error))))
}

}

  render() {
    return(
      <div>
         <div className="szukajka">
        <input type="text" placeholder="Search Albums..." onChange={this.search.bind(this)}/><br/>
       search: {this.state.searchValue}<br/>
       </div>
       { this.state.trackList.map((element, index)=>{
          return(
            <li  key={index}>
           Author: {element.artists.map((element2, index) => { 
             return(<span key='index' >{element2.name}, </span>)})}<br/>
            Title: <strong className="track-name">" {element.name} "</strong><br/>
            <img src={element.album.images[2].url}
            height="40px" width="40px" alt=" " /><br/>
            <img  alt=" "  src="https://images.vexels.com/media/users/3/135176/isolated/preview/a6508e565d25ab01f79a35c4319e0083-play-button-flat-icon-by-vexels.png" width="50px" height="50px" onClick={this.listen.bind(this, element.preview_url)}/><br/><br/>
            {/* <iframe src={element.preview_url} height="25px" className="frames"></iframe><br/><br/><br/> */}
            </li>
            )
          }
        )}
      </div>
    )
  }
}

export default FindAlbum;
