import React from 'react';
import axios from 'axios';
import FindAlbum from './FindAlbum'



class Service extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      albumList:[],
      searchValueToExist: '',
      notFindAlbum: 'notexist',
      timeout: 0
    }
  }

search(e){
  const value = e.currentTarget.value
  this.setState({
    searchValueToExist: value
  })
  if(this.state.timeout) clearTimeout(this.state.timeout);
  this.state.timeout = setTimeout(() => {
  this.setState({
    searchValue: value
  })
  if(value === ''){
  this.setState({
  albumList: []
})
}else{
  this.getList(value)
}
}, 400);
}
getList = (value) =>{
  axios({
    url: `https://api.spotify.com/v1/search?q=${value}&type=album&limit=10`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>{ 
    this.setState({
      albumList: resp.data.albums.items
     })
     this.exist()
  }).catch(error => (new Error(console.log(error))))
}
listen(url){
  if(url != null){
window.open(url)
}
}

showSongs(index, id){
const div= document.getElementById(index);

if(this.state.timeout) clearTimeout(this.state.timeout);
  this.state.timeout = setTimeout(() => {
if(div.innerHTML === ""){
  axios({
    url: `https://api.spotify.com/v1/albums/${id}/tracks`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>{ 
   resp.data.items.map(element => {
     if(element.preview_url != null){
    return(div.innerHTML = div.innerHTML + `<li>  <a  class="name-songs-linked" style="color:blue" href=${element.preview_url} target="_blank"> ${element.name} </a>  </li><br/>`)
  }else{
    return(div.innerHTML = div.innerHTML + `<li> ${element.name} </li><br/>`)
  }})

  }).catch(error => (new Error(console.log(error))))
}
}, 400);
}
changeButton = (id) =>{
  document.getElementById(id).style.display = "none";
  document.getElementById(id +'a').style.display = "block";
}
openList = (index, id) =>{
  this.showSongs(index, id)
 // changeButton()
 this.changeButton(id)
}
closeList = (index, id) =>{
  // const div= document.getElementById(index);
  document.getElementById(index).innerHTML = "";
  document.getElementById(id).style.display = "block";
  document.getElementById(id +'a').style.display = "none";
}


exist =() =>{
  if(this.state.albumList.length <= 0 && this.state.searchValueToExist.length > 0){
  this.setState({
    notFindAlbum: 'exist'
  })
  }else{
    this.setState({
      notFindAlbum: 'notexist'
    })
}
}
test2000 = ()=>{
   console.log('dziala')
  }


  render() {
    return(
        <FindAlbum  dura={this.test2000} search={this.search} notFindAlbum={this.state.notFindAlbum} searchValueToExist={this.state.searchValueToExist} albumList={this.state.albumList}/>
    )
  }
}

export default Service;
