import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "BQAcXtfpyrAv9bl1uirH5hcIOXaY8fDQOQMSXm-C7c7nH6OomZZq2MS0St2b7hn03Xciu8ke2znHC0mhKrMg94Wjpi4gCKVC2VkT7e8P2tDR9z0gsHpAzsqX8XcN-qAAFOaBiuaMaisbdTjnk6yt7ItsNiEvfVDFCZ015HyV4b3EFnaFNIRMo9D2",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
  this.token()
}
