import axios from 'axios'

export const search = (event,state,mytoken) =>{
    const value = event.currentTarget.value
  state.searchValue = value;
    let timeouter = state.timeout
    if(timeouter) clearTimeout(timeouter);
  timeouter = setTimeout(() => {
    state.searchValue = value
  if(value === ''){
  state.trackList =  [];
  state.prev = null;
  state.next = null;
}else{
 getTracks(value,0,state,mytoken)
}
}, 400);
}



export const getTracks = (value,link,state,mytoken) =>{
  let timeouter = state.timeout
  if(timeouter) clearTimeout(timeouter);
  timeouter= setTimeout(() => {
  let url = `https://api.spotify.com/v1/search?q=${value}&type=track&limit=${state.limit}`
  if(link !== 0){
    url = link;
    const start = link.indexOf('offset=')
    const stop = link.indexOf('&limit')
    const finishoff = link.slice(start,stop)
      state.myoffset = finishoff
  }
  axios({
    url: url,
    headers:{
     'Authorization': 'Bearer ' + mytoken
    }
  }).then(resp =>{ 
    state.trackList = [];
    resp.data.tracks.items.map(element =>{
      state.trackList.push(element)
    })
    state.prev = resp.data.tracks.previous;
    state.next = resp.data.tracks.next;
    //exist()
  //  this.createfav()
  }).catch(error => (new Error(console.log(error))))
  }, 400);
}

