import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "BQBDWJ7VNgY4cqdJMKdhAs4N4bCDc6Tl0Y00SPxvxxoutzcJa9IZKD6YDgkwjPDg_Sa1yRjRIYtExGVdPsY",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
  this.token()
}
token = () =>{
  const CLIENT_ID = '1f1b95fa237c478ea8fed693c7793755'
  const CLIENT_SECRET = '2ee6203d945745c6836a13841da053a6'
  const API_URL = "https://accounts.spotify.com/api/token";
  axios({
    method: "POST",
    url: API_URL,
    headers:{
      "Authorization": "Basic MWYxYjk1ZmEyMzdjNDc4ZWE4ZmVkNjkzYzc3OTM3NTU6MmVlNjIwM2Q5NDU3NDVjNjgzNmExMzg0MWRhMDUzYTY="
    },
    data:{
      'grant_type':'authorization_code',
      'code': 'AQABYXwFRIvHLoAr1LLLAXEJZBM79qNRAgw-tszxDL3A-9__JB6yXKpNkRZ_6sC_PAleqOvhtV1BUTpuHB6wMkI4ghUdZeKqF3wZIgLJvmGse-zzrzgFgQlAI9i6yhIDIsy_aIH3amYjUbLgX65hCKeol4BAEY7_7ycaxF6AcWltR99xfTwnE2NXM5y6RWTGomVA',
      'redirect_uri':'https%3A%2F%2Fapi-university.com%2F'
    }
  }).then(resp =>{
    this.setState({
      mytoken: resp
    })
  })

  // axios({
  //   method: 'post',
  //   url: `https://accounts.spotify.com/api/token`,
  //   data: { // in axios data is the body request
  //     grant_type: 'authorization_code',
  //     code: 'AQABYXwFRIvHLoAr1LLLAXEJZBM79qNRAgw-tszxDL3A-9__JB6yXKpNkRZ_6sC_PAleqOvhtV1BUTpuHB6wMkI4ghUdZeKqF3wZIgLJvmGse-zzrzgFgQlAI9i6yhIDIsy_aIH3amYjUbLgX65hCKeol4BAEY7_7ycaxF6AcWltR99xfTwnE2NXM5y6RWTGomVA', // code I'm receiving from https://accounts.spotify.com/authorize
  //     redirect_uri: 'http://localhost:3000/'
  //   },
  //   headers: {
  //     'Content-Type': 'application/x-www-form-urlencoded',
  //     'Authorization': 'Basic ' + Buffer.from(`${CLIENT_ID}:${CLIENT_SECRET}`).toString('base64') // client id and secret from env
  //   }
  // })
 
}

  render() {
    return(
        <div className="App">
        <div className="App-Menu">
        <AppHeader selectAction = {this.selectAction}/>
        </div>
       <div className="App-Contener">
       <Content action={this.state.action} mytoken={this.state.mytoken}/>
       </div>
        </div>
      )
  }
}

export default App;
