import React from 'react';
import axios from 'axios';
import { get } from 'https';
import SingleTrack from './SingleTrack';
// import {listen} from './Service'


class SingleAlbum extends React.Component {
    constructor(props){
        super(props);
        this.state ={
          timeout: 0,
          refresh: ''
        }
    }

    heart = ((id) => {
      let favourite = [];
      let heartTrue = 0;
      if(localStorage.getItem('favourite')){
        favourite = JSON.parse(localStorage.getItem('favourite'));
        favourite.forEach(element=>{
          if(element === id){
            heartTrue = 1;
          }
        })
      }
      if(heartTrue){
        return(`<img  alt=" "  class="Single-Track-Button-Favourite" src="./img/red.png"  onclick=${this.test(id)} />`)
      }else{
        return(`<img  alt=" " class="Single-Track-Button-Favourite" src="./img/white.png" onclick=${this.changeFavourite.bind(this,id)} />`)
      }
    })

    test = () =>{
      console.log('dura')
    }

    changeFavourite = (id) =>{
      if(!localStorage.getItem('favourite')){
        const favourite= [id]
        localStorage.setItem("favourite", JSON.stringify(favourite));
      }else{
        const favourite = JSON.parse(localStorage.getItem('favourite'));
        let favouriteTrue = 0;
        let favouriteIndex;
        
        for(let i=0; i< favourite.length; i++){
          if(favourite[i] === id){
            favouriteTrue = 1;
            favouriteIndex = i;
            break;
          }
        } 
        if(!favouriteTrue){
          favourite.push(id)
          localStorage.setItem("favourite", JSON.stringify(favourite))
        }
        else{
          favourite.splice(favouriteIndex, 1)
          localStorage.setItem("favourite", JSON.stringify(favourite));
        }
      }
    this.setState({
      refresh: ''
    })
  }

      //POBIERA  INFORMACJE O ALBUMIE I ZWRACA  JAKO ELEMENT LISTY//
    showSongs(index, id){
      const div= document.getElementById(index);
      let timeouter = this.state.timeout
      if(timeouter) clearTimeout(timeouter);
      timeouter = setTimeout(() => {
        if(div.innerHTML === ""){
          axios({
            url: `https://api.spotify.com/v1/albums/${id}/tracks`,
            headers:{
            'Authorization': 'Bearer ' + this.props.mytoken
            }
          }).then(resp =>{ 
            const infoBox = document.querySelector('.Find-Albums-Album-Tracks')
            infoBox.innerHTML = '';
            resp.data.items.map(element =>{
              return(infoBox.innerHTML= infoBox.innerHTML +
                `
                <div class="Find-Album-Tracks-Name-Box">
                <span class="Find-Album-Single-Tracks-Name">${element.name}</span>
                <div class="Find-Album-Single-Track-Icon-Box">
                  ${this.heart(element.id)}
                <a onClick={this.soundcloud.bind(this,parentElement.name)} ><img src='./img/soundcloud-icon.png' class="Find-Album-Single-Track-Comunity-Portal-soundcloud" alt=" " /></a>
                <a onClick={this.spotify.bind(this, parentElement.external_urls.spotify)} ><img src='./img/spotify-icon.png' class="Find-Album-Single-Track-Comunity-Portal-spotify" alt=" " /></a>
                <a onClick={this.yt.bind(this,parentElement.name)} ><img src='./img/yt-icon.png' class="Find-Album-Single-Track-Comunity-Portal-yt" alt=" " /></a>
              </div>
                </div>
                `
              )
            })
          }).catch(error => (new Error(console.log(error))))
        }
      }, 400);
    }

      //ZMIANA PRZYCISKU ROZWIJANIA LISTY//

      //ROZWIJANIE LISTY //
    openList = (index, id) =>{
      this.showSongs(index, id)
    }
    


    
    render(){
      const parentElement = this.props.parentElement;
      const parentIndex = this.props.parentIndex;
      return(
        <div  key={parentIndex} className="Find-Albums-One-Album">
       Author: {parentElement.artists.map((element2, index) => { 
         return(<span key={index +1} >{element2.name}, </span>)})}<br/>
        Album: <strong className="album-name">" {parentElement.name} "</strong><br/>
        <img src={parentElement.images[2].url}
        height="40px" width="40px" alt=" " /><br/>
        <button id={parentElement.id} key= {parentIndex +2} className="main-button" onClick={this.openList.bind(this, parentIndex, parentElement.id)}>tracks from this album</button><br/>
        <ol id={parentIndex} className="to-kill"></ol><br/><br/><br/>
        </div>
        )

    }   
}

export default SingleAlbum;
