import React from 'react';
import axios from 'axios';




class FindGenres extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      searchList:[],
      typeTracks:[],
    }
  }
  componentDidMount(){
    axios({
      url: `https://api.spotify.com/v1/recommendations/available-genre-seeds`,
      headers:{
       'Authorization': 'Bearer ' + this.props.mytoken
      }
    }).then(resp =>
      { this.setState({
        typeTracks: resp.data.genres
     })
     console.log(resp.data.genres)
    }).catch(error => (new Error(console.log(error))))
  }

search(e){
  const value = e.currentTarget.value
  this.setState({
    searchValue: value
  })
  if(value === ''){
  this.setState({
  searchList: []
})
}else{
  axios({
    url: `https://api.spotify.com/v1/search?q=genre:${value}&type=track`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>
    { this.setState({
    searchList: resp.data.tracks.items
   })
   console.log(resp.data.tracks.items)
  }).catch(error => (new Error(console.log(error))))
}

}
listen(url){
  if(url != null){
window.open(url)
}
}

  render() {
    return(
      <div>
        <div className="szukajka">
       <select onChange={this.search.bind(this)}>
         {this.state.typeTracks.map(element=>{
           return(
             <option>{element}</option>
           )
         })}
       </select>
       </div>
       <ol>
         
       </ol>
      </div>
    )
  }
}

export default FindGenres;
