import React from 'react';
import axios from 'axios';




class FindSound extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      searchList:[]
    }
  }

async search(e){
  const value = e.currentTarget.value
  this.setState({
    searchValue: value
  })
  if(value === ''){
  this.setState({
  searchList: []
})
}else{
  await axios({
    url: `https://api.spotify.com/v1/search?q=${value}&type=track&limit=10`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>{ this.setState({
    searchList: resp.data.tracks.items
   })
   console.log(this.state.searchList)
  }).catch(error => (new Error(console.log(error))))
}

}
listen(url){
  if(url != 0)
window.open(url)
}

  render() {
    return(
      <div>
        <input type="text" placeholder="Search Tracks..." onChange={this.search.bind(this)}/><br/>
       search: {this.state.searchValue}<br/>
       <ol>
       {
         this.state.searchList.map((element, index)=>{
            return(
            
            <li  key={index}>
             <img src={element.album.images[2].url}
            height="40px" width="40px" alt=" " /><br/>
            title: {element.name}<br/>
            <img src="https://images.vexels.com/media/users/3/135176/isolated/preview/a6508e565d25ab01f79a35c4319e0083-play-button-flat-icon-by-vexels.png" width="50px" height="50px" onClick={this.listen.bind(this, element.preview_url)}/>
            {/* <iframe src={element.preview_url} height="25px" className="frames"></iframe><br/><br/><br/> */}
            </li>
            )}
        )}
       </ol>
      </div>
    )
  }
}

export default FindSound;
