import React from 'react';
import axios from 'axios';
import SingleTrack from './SingleTrack';
import {search,getTracks} from './Service'


class FindSound extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      trackList:[],
      limit: 5,
      prev: null,
      next: null,
      myoffset: 'offset=0',
      notFindTracks: 'notexist',
       timeout: 0,
       refresh : ""
      
    }
  }
limit(e){
  const valueLimit = e.currentTarget.value
  if(this.state.searchValue === []){
    console.log('please write name of tracks')
    this.setState({
    trackList: []
  })
  }else{
    this.setState({
      limit: valueLimit
    })
    axios({
      url: `https://api.spotify.com/v1/search?q=${this.state.searchValue}&type=track&${this.state.myoffset}&limit=${valueLimit}`,
      headers:{
       'Authorization': 'Bearer ' + this.props.mytoken
      }
    }).then(resp =>{ this.setState({
      trackList: resp.data.tracks.items,
      prev: resp.data.tracks.previous,
      next: resp.data.tracks.next
     })
     this.createfav()
    }).catch(error => (new Error(console.log(error))))
  }
}

searching(event){
  const dura = new Promise(function(resolve,reject){
      resolve(search(event, this.state,this.props.mytoken));
  })
  console.log(dura)
  dura.then(() =>
    this.refresh()
  )

  dura.catch(console.log('error'))


}

refresh = () =>{
  let timeouter = this.state.timeout
  if(timeouter) clearTimeout(timeouter);
  timeouter = setTimeout(() =>{this.setState({
    refresh: ""
  }) },1000)
}

getTn =(event) =>{
  getTracks(event,this.state.next,this.state,this.props.mytoken)
  this.refresh() 
}

getTp =(event) =>{
  getTracks(event,this.state.prev,this.state,this.props.mytoken)
  this.refresh()
}

listen(url){
  if(url != null){
window.open(url)
}
}


  render() {
    let prevButton = "", nextButton = "";
    if(this.state.prev !==null){
      prevButton = <button onClick={this.getTp.bind(this)}>Previous</button>
    }
    if(this.state.next !==null){
      nextButton = <button onClick={this.getTn.bind(this)}>Next</button>
    }
    return(
      <div>
        <div className="szukajka">
        <input type="text" placeholder="Search Tracks..." onChange={this.searching.bind(this)}/>
        <select onChange={this.limit.bind(this)}>
          <option>5</option>
          <option>10</option>
          <option>15</option>
          <option>20</option>
          <option>25</option>
          <option>30</option>
        </select>
        <br/>
       score:<br/>

       </div>
       <span className={this.state.notFindTracks}><strong className="title">{this.state.searchValue} </strong> not exist</span>
       <ol>
       {
         this.state.trackList.map((element, index)=>
         <SingleTrack  key={index} parentElement={element} parentIndex={index}/>
        )}
       </ol>
      {prevButton}
      {nextButton}
    </div>
    )
  }
}

export default FindSound;
