import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
  this.token()
}

token = () =>{
  const auth = btoa('1f1b95fa237c478ea8fed693c7793755')+":"+btoa('2ee6203d945745c6836a13841da053a6')+""
  
  
  axios({
    method: 'POST',
    url: "https://accounts.spotify.com/api/token",
    data: {
      grant_type: 'client_credentials',
    },
    headers:{
      Authorization: auth,
    }
  }).then(resp => {
     console.log(resp)
  })
  .catch(error => console.log(new Error(`Wystąpił problem ${error}`)))
}
