import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentWillMount(){
  this.token()
}
token = () =>{
  axios.get({
    method: "POST",
    url: 'https://accounts.spotify.com/api/token&grant_type=client_credentials',
    data:{
      grant_type: 'client_credentials'
    },
    headers:{
      Authorization: "Basic " + btoa('1f1b95fa237c478ea8fed693c7793755') + ":" + btoa('2ee6203d945745c6836a13841da053a6')
    }
  }


  ).then(resp =>{

  })
  const activeToken = `BQB6aEWndZPQbBIo5B0UrH0DCktn9PlAuB-rkMYp_Ngfjgf_4Z0c1ZxkhKj0K64hNNLJTgheedQ9AvJvPTIzeTdQ-iS89fLLL9nqfoRPVjpG8bmrZBgHBzEO5cEZ4OpyO5aYaMzByeSPMBdIn6oXpae3myhl7BmLFN1jQZ4pR6QFinp9LGpsUzXp`
this.setState({
  mytoken: activeToken
})
}
 
  render() {
    return(
        <div className="App">
        <div className="App-Menu">
        <AppHeader selectAction = {this.selectAction}/>
        </div>
       <div className="App-Contener">
       <Content action={this.state.action} mytoken={this.state.mytoken}/>
       </div>
        </div>
      )
  }
}

export default App;
