import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
 this.token()
}
// checkToken = () =>{
// setTimeout(() => {
//   this.token();
// }, 300000);
// }

token = () =>{
  axios({
    method: "GET",
    url: 'https://accounts.spotify.com/authorize?client_id=1f1b95fa237c478ea8fed693c7793755&response_type=token&redirect_uri=http%3A%2F%2Flocalhost%3A3000%2F'
  }).then(resp =>{
    console.log(resp.request.responseURL)
    const url = String(window.location)
    const start = url.indexOf('access_token=') +13
    const stop = url.indexOf('&token_type')
    const finishoff = url.slice(start,stop)
   console.log(finishoff)
  })
 
}


  render() {
    return(
        <div className="App">
        <div className="App-Menu">
        <AppHeader selectAction = {this.selectAction}/>
        </div>
       <div className="App-Contener">
       <Content action={this.state.action} mytoken={this.state.mytoken}/>
       </div>
        </div>
      )
  }
}

export default App;
