import React from 'react';
import {Link} from 'react-router-dom'


// KOMPONENT  WYSWIETLAJACY MENU //

class AppHeader extends React.Component {
  constructor(){
    super()
    this.state= {
      refresh: ''
    }
  }
  defaultLocation = (e) =>{
 this.setState({
   refresh: ''
 })
  }

  targetButton =(value) =>{
    if(window.location.pathname === value){
      'Menu-Buttons Default-Location'
    }
  }
 
  render() {
    return(
      <div className='App-Menu'>
        <Link id="Homepage" className={this.targetButton()}  onClick={this.defaultLocation.bind(this,window.location.pathname)} to="/" >Homepage</Link>
        <Link id="FindSound" className="Menu-Buttons" onClick={this.defaultLocation.bind(this,window.location.pathname)}  to="/findsound">Find Sound</Link>
        <Link className="Menu-Buttons" to="/findartist">Find Artist</Link>
        <Link className="Menu-Buttons" to="/findgenres">Find Genres</Link>
        <Link className="Menu-Buttons" to="/findalbum">Find Album</Link>
        <Link className="Menu-Buttons" to="/favourite">Favourite</Link>
        </div>  
    )
  }
}

export default AppHeader;
