import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'




class App extends React.Component {
  constructor(){
    super();
    this.state = {
      action: "findSound"
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
 
  render() {
    return(
        <div className="App">
        <AppHeader selectAction = {this.selectAction}/>
        <Content action={this.state.action}/>
        </div>
      )
  }
}

export default App;
