getT =() =>{
  getTracks(this.state.searchValue,this.state.prev,this.staet,this.props.mytoken)
  let timeouter = this.state.timeout
  if(timeouter) clearTimeout(timeouter);
timeouter = setTimeout(() => {
  this.setState({
    refresh: ""
  })},400)
  console.log(this.state.trackList)
}

