import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "BQAoFjo3PUNUC5scox0uu0_hLswAX-eZTOrFYSl9SxktYMiZlNvxiyhint2-fPJX0m0Oz20ZEmBDkAw4Jak5-U1t7OptIiFWkhbp4FhlHtmn1frjE10BeJLq0aJru1XQ02hAOvn3is1Q0m-zufhov94D3JmFL_y5aSC9mFOs344OEWNyB46s_yw9",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
 this.checkToken()
}
checkToken = () =>{
setTimeout(() => {
  this.token();
}, 3000);
}

token = () =>{
  axios({
    method: "GET",
    url: 'https://accounts.spotify.com/authorize?client_id=1f1b95fa237c478ea8fed693c7793755&response_type=token&redirect_uri=http%3A%2F%2Flocalhost%3A3000%2F'
  }).then(resp =>{
    window.location = resp.request.responseURL
  })
 
}


  render() {
    return(
        <div className="App">
        <div className="App-Menu">
        <AppHeader selectAction = {this.selectAction}/>
        </div>
       <div className="App-Contener">
       <Content action={this.state.action} mytoken={this.state.mytoken}/>
       </div>
        </div>
      )
  }
}

export default App;
