import React from 'react';
import AppHeader from './AppHeader'
import Content from './Content'
import axios from 'axios';



class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      action: "homePage",
     mytoken: "",
    }
  }
selectAction = (action) =>{
  this.setState({
    action
  })
}
componentDidMount(){
  this.token()
}
token = () =>{
  axios.post({
    url: 'https://accounts.spotify.com/api/token',
    data:{
      grant_type: 'client_credentials',
    },
    headers:{
      "Authorization": "Basic " + new Buffer('1f1b95fa237c478ea8fed693c7793755' + ":" + '2ee6203d945745c6836a13841da053a6').toString('base64')
    }
  }

  ).then(resp =>{
    console.log(resp)
  })
 
}
 
  render() {
    return(
        <div className="App">
        <div className="App-Menu">
        <AppHeader selectAction = {this.selectAction}/>
        </div>
       <div className="App-Contener">
       <Content action={this.state.action} mytoken={this.state.mytoken}/>
       </div>
        </div>
      )
  }
}

export default App;
