import React from 'react';
import axios from 'axios';




class FindArtist extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      searchValue: [],
      artistList:[]
    }
  }

search(e){
  const value = e.currentTarget.value
  this.setState({
    searchValue: value
  })
  if(value === ''){
  this.setState({
  artistList: []
})
}else{
  axios({
    url: `https://api.spotify.com/v1/search?q=${value}&type=artist&limit=10`,
    headers:{
     'Authorization': 'Bearer ' + this.props.mytoken
    }
  }).then(resp =>{ this.setState({
    artistList: resp.data.artists.items
   })
   console.log(this.state.artistList)
  }).catch(error => (new Error(console.log(error))))
}

}
listen(url){
  if(url != null){
window.open(url)
}
}

  render() {
    return(
      <div>
        <div className="szukajka">
        <input type="text" placeholder="Search Artists..." onChange={this.search.bind(this)}/><br/>
       search: {this.state.searchValue}<br/>
       </div>
       <ol>
       {
         this.state.artistList.map((element, index)=>{
            if(element.preview_url != null){
          
          return(
            
            <li  key={index}>
            
            title: {element.name}<br/>
            {/* <img src={element.images[2].url}
            height="40px" width="50px" alt=" " /><br/> */}
            <img  alt=" "  src="https://images.vexels.com/media/users/3/135176/isolated/preview/a6508e565d25ab01f79a35c4319e0083-play-button-flat-icon-by-vexels.png" width="50px" height="50px" onClick={this.listen.bind(this, element.preview_url)}/><br/><br/>
            {/* <iframe src={element.preview_url} height="25px" className="frames"></iframe><br/><br/><br/> */}
            </li>
            )}else{
              return(
                <li  key={index}>
                name: <strong className="artist-name">{element.name}</strong><br/>
                <img src={element.images[2].url}
                height="40px" width="40px" alt=" " /><br/>
                total followers: {element.followers.total}<br/><br/><br/>
                </li>
                )
            }
          }
        )}
       </ol>
      </div>
    )
  }
}
export default FindArtist;
